-- заполнение таблицы media
-- обновление поля metadata
-- обновление поля filename
-- обновление crerated_at и udated_at


UPDATE media SET metadata = CONCAT(
  '{"', 
  'owner', 
  '":"', 
  (SELECT CONCAT(first_name, ' ', last_name) FROM users WHERE id = user_id),
   '"}');

UPDATE media SET filename = (SELECT CONCAT('https://dropbox/vk/media/', first_name, '_', last_name, '100') FROM users u WHERE user_id = u.id);

UPDATE media SET updated_at = (SELECT updated_at FROM users u WHERE u.id = user_id), 
		created_at = (SELECT created_at FROM users u WHERE u.id = user_id); 
 
